          triggerOn: 'mousemove',
          formatter: (params) => {
            switch (params.data.type) {
              case 'pod':
                return `name:${params.data.name}<br>类型:${params.data.podtype}<br>任务ID:${params.data.taskid}<br>状态:${params.data.status.ready ? '正常' : '异常'}`
              case 'node':
                return `name:${params.data.name}<br>CPU:${params.data.cpu}%<br>内存:${params.data.memory}%<br>状态:${params.data.status == 'Ready' ? '正常' : '异常'}`
            }
          }
        },
        series: [
          {
            orient: 'TB',
            type: 'tree',
            id: 0,
            name: 'tree1',
            data: [data],
            top: '10%',
            left: '8%',
            bottom: '22%',
            right: '20%',
            symbolSize: 45,
            symbol: 'triangle',
            edgeShape: 'polyline',
            edgeForkPosition: '63%',
            initialTreeDepth: 3,
            lineStyle: {
              width: 2
            },
            label: {
              backgroundColor: '#fff',
              position: 'bottom',
              verticalAlign: 'middle',
              align: 'center',
              formatter: (params) => {
                switch (params.data.type) {
                  case 'node': return params.data.name
                  case 'pod': return params.data.name.slice(0, 6)
                }
              }
            },
            leaves: {
              label: {
                position: 'bottom',
                verticalAlign: 'middle',
                align: 'center'
              }
            },
            emphasis: {
              focus: 'descendant'
            },
            expandAndCollapse: true
          }
        ]
      }
    }
  },
  async created() {
    this.loading=true
    await this.init(data)
    await this.getPods(data)
    console.log(data)
    this.fillsymbol(data)
    this.fillCollapse(data)
    this.loading=false
    this.option.series[0].data = [data]
    setInterval(() => {
      this.init(data).then(() => { return this.getPods(data) }).then(() => { this.fillCollapse(data); this.fillsymbol(data); this.option.series[0].data = [data]; console.log(123) })
    }, 3 * 60000)
  },
  methods: {
    async init(data) {
      var nodes = await getNodesT()
      console.log(nodes)
      var master = ''
      var children = []
      for (const k in nodes) {
        console.log(k)
        if (this.collapseStatus[k] == undefined) { this.collapseStatus[k] = true }
        if (nodes[k].type == 'master') {
          master = k
        } else {
          children.push({
            name: k,
            position: nodes[k].label.includes('云端') ? nodes[k].label.includes('GPU') ? 'gpu' : 'cloud' : 'edge',
            status: nodes[k].status,
            cpu: nodes[k].cpu_percent,
            memory: nodes[k].mem_percent,
            children: [],
            type: 'node'
          })
        }
      }

      data['name'] = master
      data['position'] = 'master'
      data['type'] = 'node'
      data['status'] = nodes[master].status
      data['cpu'] = nodes[master].cpu_percent
      data['memory'] = nodes[master].mem_percent
      data['children'] = children
      console.log(data)
    },
    async getPods(data) {
      const pod = {}
      var tasks = await getTasks()

      console.log(tasks.data.data)
      for (const i in tasks.data.data) {
        const task = tasks.data.data[i]
        for (const j in task.Pods) {
          const t = task.Pods[j]
          if (pod[t.Node] != undefined) {
            pod[t.Node].push({
              name: t.PodId,
              podtype: t.Type,
              type: 'pod',
              taskid: task.TaskId,
              status: await this.getStatus(task.TaskId, t.PodId)
            })
          } else {
            pod[t.Node] = [{
              name: t.PodId,
              podtype: t.Type,
              type: 'pod',
              taskid: task.TaskId,
              status: await this.getStatus(task.TaskId, t.PodId)
            }]
          }
        }
      }
      console.log(pod)
      for (const i in data.children) {
        data.children[i].children = pod[data.children[i].name]
      }
    },
    async getStatus(taskid, podid) {
      console.log(taskid, podid)
      const podname = await getPodName({ podid: podid, taskid: taskid })
      const status = await getPodStatus({ podname: podname.data.Data })
      return status.data.Data
    },
    test(e) {
      if (this.collapseStatus[e.name] != undefined) {
        console.log(e)
        this.collapseStatus[e.name] = !this.collapseStatus[e.name]
      }
      console.log(this.collapseStatus)
    },
    fillsymbol(data) {
      if (data.type == 'pod') {
        data.symbol = symbol.pod
        return
      } else {
        data.symbol = symbol[data.position]
        for (const i in data.children) {
          this.fillsymbol(data.children[i])
        }
      }
    },
    fillCollapse(data) {
      if (data.type == 'pod') {
        return
      } else {
        data.collapsed = this.collapseStatus[data.name]
        for (const i in data.children) {
          this.fillCollapse(data.children[i])
        }
      }
    }

  }
}
